sys_ui_page.client_script:
$j = jQuery.noConflict();

$j('#myframe').load(function() {
    $j("#myframe").contents().find("a").attr("target", "_parent");
});

getAssetInfo();

function getAssetInfo() {

    var bridgeAjax = new GlideAjax("Cisco_ClientServerBridge");
    bridgeAjax.addParam('sysparm_name', "getAssetDrilldownInfo");
    bridgeAjax.getXML(getAssetDrilldownInfoResponse);
}

function getAssetDrilldownInfoResponse(response) {
    var answer = response.responseXML.documentElement.getAttribute("answer");
    var result = JSON.parse(answer);

    var title = ["Physical Type", "Serial Number Status", "Platform Diversification (Top 15)"];

    for (var j = 0; j < result.length; j++) {

        var xAxis = {};
        var categories = [];
        var data_array = [];
		var series_data = [];

        for (var i in result[j]) {
            data_array.push({
                name: i + ' [' + result[j][i] + ']',
                y: parseInt(result[j][i])
            });
			series_data.push(parseInt(result[j][i]));
            categories.push(i);
        }

		alert(JSON.stringify(data_array));
        xAxis.categories = categories;

        if (j != 2)
			x_545898_cisco_bcs.Cisco_RenderGraph.renderPieChart("report"+j, title[j], data_array, "pie", xAxis);
        else {
            var series_obj = [{"data": series_data, "showInLegend": false}];
            x_545898_cisco_bcs.Cisco_RenderGraph.renderBarChart("report"+j, title[j], series_obj, "column", xAxis, "Count");
        }
    }
}
