var $j = jQuery.noConflict();

function myFunction() {

    $j("input").click(function() {
        $j("#error_message").html('');
		});

    var json = [];
    var submit_flag = true;
    var f = 0;
    var comments = [];
    var global_comments = 0;
    var searchEles = document.getElementById("Prince").children;

    for (var i = 0; i < searchEles.length; i++) {

        if (searchEles[i].tagName == 'INPUT') {
            var check = searchEles[i].checked == true;
            if (check == true) {
                json.push(searchEles[i].value);
                if (searchEles[i].value < 3 && document.getElementById("comment1").value == "") {
                    submit_flag = false;
                    f = 1;
                    document.getElementById("error_message1").innerHTML = "Please fill the comment box.";
                    document.getElementById("comment1").style = "outline: solid;outline-width: 1.5px;outline-color: red;"
                    break;
                }
				document.getElementById("comment1").style = ""
                if (document.getElementById("comment1").value) {
                    comments.push(document.getElementById("comment1").value);
                } else {
                    comments.push("-");
                }
                f = 1;
                break;
            }
        }
    }
    if (f == 0) {
        global_comments = 1;
    }
    f = 0;
