function device_drilldown(method,tablename,groupBy,reportID,title,graphType) {

	var bridge = new GlideAjax("Cisco_ClientServerBridge");
	bridge.addParam('sysparm_name', method+"");
	bridge.addParam('sysparm_table_name', tablename+"");
	bridge.addParam('sysparm_group_by', groupBy+"");
	bridge.addParam('sysparm_graph_type', graphType+"");
	bridge.getXML(devices);

	function devices(response) {
		var categories = [];
		var value = [];
		var answer = response.responseXML.documentElement.getAttribute("answer");
		answer = JSON.parse(answer);
		var count = 0;

		//alert(JSON.stringify(answer));
		var data_array = [];
		for (var i in answer) {
			count++;
			if(graphType != "column"){
			data_array.push({
				name: i + ' [' + parseInt(answer[i]) + ']',
				y: parseInt(answer[i])
			});
			}else{
				var json = {};
				categories.push(i);
				json.name = i;
				json.data = [{x:count,y:parseInt(answer[i]),color:"#0000FF"}];
				data_array.push(json);
				if(count==10)
					break;
			}
		}
		//alert(JSON.stringify(data_array));
		var xAxis = {};
		xAxis.categories = categories;
		alert(categories);
		renderUIScript(reportID,title,data_array,graphType,xAxis);
	}
}
function renderUIScript(reportID,title,data,graphType,xAxis) {

	if(graphType == "pie") {
		ScriptLoader.getScripts('x_545898_cisco_bcs.Cisco_RenderGraph.jsdbx', function() {
			x_545898_cisco_bcs.Cisco_RenderGraph.renderPieChart(reportID,title,data,graphType,xAxis);
		});
	}

	if(graphType == "column") {
		ScriptLoader.getScripts('x_545898_cisco_bcs.Cisco_RenderGraph.jsdbx', function() {
			x_545898_cisco_bcs.Cisco_RenderGraph.renderBarChart(reportID,title,data,graphType,xAxis);
		});
	}
}
